fn basic() { let mut app = App::default(); let owner = Addr::unchecked("addr0001"); let admin1 = Addr::unchecked("admin1"); let admin2 = Addr::unchecked("admin2"); let admin3 = Addr::unchecked("admin3"); let code_id = AdminContractCodeId::store_code(&mut app); let contract = code_id .instantiate( &mut app, &owner, vec![admin1.to_string(), admin2.to_string()], "Cw20 contract", None, ) .unwrap(); let resp = contract.admin_list(&app).unwrap(); assert_eq!( resp, AdminListResp { admins: vec![admin1.to_string(), admin2.to_string()] } ); let resp = contract .add_member(&mut app, &admin1, admin3.to_string()) .unwrap(); let wasm = resp.events.iter().find(|ev| ev.ty == "wasm").unwrap(); assert_eq!( wasm.attributes .iter() .find(|attr| attr.key == "action") .unwrap() .value, "add_member" ); let admin_added: Vec<_> = resp .events .iter() .filter(|ev| ev.ty == "wasm-admin_added") .collect(); assert_eq!(admin_added[0], &Event::new("wasm-admin_added").add_attribute("_contract_addr", contract.addr()).add_attribute("addr", Addr::unchecked("admin3"))); let resp = contract.admin_list(&app).unwrap(); assert_eq!( resp, AdminListResp { admins: vec![admin1.to_string(), admin2.to_string(), admin3.to_string()] } );
